import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
import { darkslateblue } from 'color-name';

const objeto1 = {
    nome: "Breno",
    email: "brunopcem@gmail.com",
}

function Welcome(props) {
    let{nome, title} = props
    return <h1>Bem Vindo, {props.nome}</h1>;
}

class Clock extends
React.COmponent {
    constructor(props){
        super(props);
        this.state = {date: new Date()}
    }

    componentDidMount(){
        this.timerID = setInterval(
            () => this.tick(),
            1000
        );
    }

    tick(){
        this.setState({
            date: new Date()
        });
    }

    render(){
        return (
            <div>
                <h2> Hor√°rio: {this.state.date.toLocaleTimeString()}.</h2>
            </div>
        );
    }
}

